import './User.scss';
import React from 'react';
import Nav from "../../components/header/Nav";
import Footer from "../../components/header/footer/footer";
import "../home/home.scss";
import ToggleButton from '@mui/material/ToggleButton';
import { useState } from 'react';
import { Link } from "react-router-dom";
import { Box } from '@mui/system';
import BreadCrumbs from '../../components/breadcrumbs/breadCrumbs';
import AssignmentIcon from '@mui/icons-material/Assignment';
import LockClockIcon from '@mui/icons-material/LockClock';
import AssignmentIndRoundedIcon from '@mui/icons-material/AssignmentIndRounded';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
function User(){
    const [alignment, setAlignment] = React.useState('profile');

    const handleChange = (event, newAlignment) => {
      setAlignment(newAlignment);
    };
    return(
        <>
        <div className="boxContainer">
         <Nav/>
         <Footer />
         <div className="profileContainer">
         <div className="bg-profileContainer">
             <div className="profileTitle">Profile</div>
            <BreadCrumbs titTle="User Profile"/>
            </div>
            <div className="bg-profileContainer">
                <div className="bg-menu">
                <ToggleButtonGroup
                    color="primary"
                    value={alignment}
                    text-transform="none"
                    exclusive
                    onChange={handleChange}>
                        <ToggleButton value="profile"> 
                            <Box  className="boxContainer">
                                <Link className="profile-link" to="/user">
                                    <AssignmentIndRoundedIcon className="iconElement" />
                                    <div className="menuElement">Profile</div>
                                </Link>
                            </Box>
                        </ToggleButton>

                        <Box className="boxContainer">
                            <Link className="profile-link" to="/user/profile">
                                <AssignmentIcon className="iconElement" />
                                <div className="menuElement">Personal Details</div>
                            </Link>
                        </Box>
                        <Box className="boxContainer">
                            <Link className="profile-link" to="/user/resetPassword">
                                <LockClockIcon className="iconElement" />
                                <div className="menuElement">Reset password</div>
                            </Link>
                        </Box>
                    </ToggleButtonGroup>
                </div>

            </div>
         </div>
         </div>
        </>
    )
}
export default User;
